import {useContext, useEffect, useState} from 'react'
import {CityContext} from '../context/CityContext'
import axios from 'axios';

const api = {
    key: "bc72320a4db7de1f5645c14cade36619",
    base: "https://api.openweathermap.org/data/2.5/",
    lat: "",
    lon: "",
};

// axios(`${api.base}weather?lat=32&lon=31&appid=${api.key}`)
//             .then((result) => {console.log(result);})

function Body() {
    const {city} = useContext(CityContext);
    const [weatherData, setWeatherData] = useState();
    const [status, setStatus] = useState(false);

    useEffect(() => {
        if (city !== ""){
            switch(city){
                case "izmir":
                    api.lat = "38.4237433";
                    api.lon = "27.1428019";
                    break;
                case "konya":
                    api.lat = "37.8727";
                    api.lon = "32.4924";
                    break;
                case "istanbul":
                    api.lat = "41.006381";
                    api.lon = "28.9758715";
                    break;
                case "ankara":
                    api.lat = "39.9207759";
                    api.lon = "32.8540497";
                    break;
                case "antalya":
                    api.lat = "36.8864752";
                    api.lon = "30.7029585";
                    break;
                default:
                    console.log("Error");
                    break;
            }

            axios(`${api.base}weather?lat=${api.lat}&lon=${api.lon}&appid=${api.key}`)
            .then((result) => {
                setWeatherData(result);
                setStatus(true);
            })

        }
    }, [city]);

  return (
    <div>
        {!status && <h3>Please select a city.</h3>}

    </div>
  )
}

export default Body
