import {useContext, useEffect} from 'react'
import {CityContext} from '../context/CityContext'
import axios from 'axios';

const api = {
    key: "bc72320a4db7de1f5645c14cade36619",
    base: "https://api.openweathermap.org/data/2.5/",
    lat: "",
    lon: "",
};

// Konya coordinates = "coord":{"lon":32.4924,"lat":37.8727}
// Izmir coordinates = "lat":38.4237433,"lon":27.1428019,"
// Istanbul coordinates = "lat":41.006381,"lon":28.9758715,"
// Ankara coordinates = "lat":39.9207759,"lon":32.8540497,"
// Antalya coordinates = "lat":36.8864752,"lon":30.7029585,"

// axios(`${api.base}weather?lat=32&lon=31&appid=${api.key}`)
//             .then((result) => {console.log(result);})

function Body() {
    const {city} = useContext(CityContext);

    // const selected = (() => {
    //     axios(`${api.base}weather?q=${city}units=metric&APPID=${api.key}`)
    //     .then((result) => {console.log(result);})
    // })

    useEffect(() => {
        if (city !== ""){
            switch(city){
                case "izmir":
                    api.lat = "38.4237433";
                    api.lon = "27.1428019";
                    break;
                case "konya":
                    api.lat = "37.8727";
                    api.lon = "32.4924";
                    break;
                case "istanbul":
                    api.lat = "41.006381";
                    api.lon = "28.9758715";
                    break;
                case "ankara":
                    api.lat = "39.9207759";
                    api.lon = "32.8540497";
                    break;
                case "antalya":
                    api.lat = "36.8864752";
                    api.lon = "30.7029585";
                    break;
            }
        }
        console.log(api);
    }, [city]);

  return (
    <div>
      
    </div>
  )
}

export default Body
